interface ProductItem{
  name: string;
  price: number;
  quantity: number;
  variant_id: string;
  variant_name: string;
}
export default function useStateWithStorage() {
  const addProduct = (item:ProductItem)=>{
    console.log(item)
    const cart = JSON.parse(localStorage.getItem('cart')||'[]');
    const exist = cart.findIndex((cartItem:ProductItem) => cartItem.variant_id = item.variant_id) > -1;
    if(exist){
      cart.map((cartItem:ProductItem) => {
        if(cartItem.variant_id = item.variant_id){
          cartItem.quantity =cartItem.quantity+ item.quantity;
        }
        return cartItem
      })
    } else {
      cart.push(item)
    }
    console.log(cart)
    localStorage.setItem('cart', JSON.stringify(cart))
  }
  return {
    addProduct
  }
}
