import { Button, Card, CardActions, CardContent, CardMedia, MenuItem, Select, SelectChangeEvent, Typography } from "@mui/material"
import { useState } from "react";
import { IProduct, IVariants } from "../models/products";
import { Currency } from "./currency";

export const ProductItem = ({item, index}: {item: IProduct, index: number})=>{
    const [selectedItem, setSelectedItem] = useState(item.variants[0]);
    const handleChange = (e: SelectChangeEvent)=>{
        const choice = item.variants?.find((v:IVariants)=>v.sku === e.target.value );
        setSelectedItem(choice || item.variants[0])
    }
    return (<Card sx={{ maxWidth: 345 }} key={index}>
        <CardMedia
          component="img"
          alt="product image"
          height="140"
          image={item.assets[0].source}
        />
        <CardContent>
          <Typography gutterBottom variant="h6" component="div">
          {item.name}
          </Typography>
          <Typography variant="body2">
          {item.description?.substring(0,100)}
          </Typography>
          {item.variants.length > 1 ? 
            <Select onChange={handleChange} size="small" value={selectedItem.sku}>
                {item.variants.map((opt:IVariants)=>(
                     <MenuItem key={opt.sku} value={opt.sku}>{opt.name} <Currency value={opt.price} /></MenuItem>
                ))}
            </Select>
            : 
            <Typography variant="body2">
            {item?.variants[0]?.name} <Currency value={item?.variants[0]?.price} />
            </Typography>
          }
        </CardContent>
        <CardActions>
          <Button size="small">Buy</Button>
        </CardActions>
      </Card>

    )
}
