import { getApolloContext, useQuery } from "@apollo/client";
import { useContext, useEffect } from "react";
import { GET_COLLECTIONS } from "../graphql/queries";
import { styled } from "styled-components";

const Card = styled.article`
  border-radius: 5px;
  width: 30%;
  background: #ffffff;
  overflow: hidden;
  span {
    height: 120px;
    display: flex;
    align-items: center;
    overflow: hidden;
  }
  img {
    width: 100%;
  }
`
interface IProduct {
  assets: {source: string}[];
  name: string;
  description: string
}
export function ProductList() {
  const {loading, error, data: {products:{items:productList}}} = useQuery(GET_COLLECTIONS,{variables: {start: 0, limit: 10}})
  //const {client} = useContext(getApolloContext());
  useEffect(()=>{
    console.log(productList)
  }, [productList])
  return <div style={{paddingTop: '15px', display: 'flex', flexWrap: 'wrap', justifyContent: 'space-evenly', width: '960px', margin: '0 auto'}}>
    {productList?.map((item:IProduct,index:number)=>{
      return (<Card key={index}>
        <span>
          <img src={item.assets[0].source} alt="product image" />
        </span>
        <h4>{item.name}</h4>
        <p>{item.description.substring(0,50)}</p>
      </Card>)
    })}
  </div>;
}
