import { Button, Card, CardActions, CardContent, CardMedia, MenuItem, Select, Typography } from "@mui/material"

interface IProduct {
    assets: {source: string}[];
    name: string;
    description: string;
    variants: {name: string, price: number; sku: string}[]
  }
export const ProductItem = ({item, index}: {item: IProduct, index: number})=>{
    return (<Card sx={{ maxWidth: 345 }} key={index}>
        <CardMedia
          component="img"
          alt="product image"
          height="140"
          image={item.assets[0].source}
        />
        <CardContent>
          <Typography gutterBottom variant="h6" component="div">
          {item.name}
          </Typography>
          <Typography variant="body2">
          {item.description?.substring(0,100)}
          </Typography>
          {item.variants.length > 1 ? 
          <Select >
            {item.variants.map((opt)=><MenuItem value={opt.sku}>{opt.name} {opt.price}</MenuItem>)}
          </Select>
          : `${item.variants[0].name} ${item.variants[0].price}`}
        </CardContent>
        <CardActions>
          <Button size="small">Buy</Button>
        </CardActions>
      </Card>

    )
}
