import { useQuery } from "@apollo/client";
import { useEffect, useState } from "react";
import { GET_COLLECTIONS } from "../graphql/queries";
import { ProductItem } from "./ProductItem";
import { Grid } from "@mui/material";
import { GridWrapper } from "./card/styles";
import { IProduct } from "../models/products";
import { Paginator } from "./paginator";

export function ProductList() {
  const {loading, data} = useQuery(GET_COLLECTIONS,{variables: {start: 0, limit: 10}})
  console.log(data)
  const [productList, setProductList] = useState(data?.products?.items || [])
  const handlePaginationChange = ()=>{}
  useEffect(()=>{
    if(!loading){
      setProductList(data?.products?.items || [])
    }
  },[loading,data])
  useEffect(()=>{
    console.log(productList)
  }, [productList])
  return <GridWrapper>
    <Grid container spacing={2} >
        {productList?.map((item:IProduct,index:number)=>{
          return (<Grid item xs={4}>
            <ProductItem item={item} index={index} />
          </Grid>)
        })}

    </Grid>
    <Paginator totalItems={data?.products?.totalItems} itemsPerPage={10} onChange={handlePaginationChange} />
  </GridWrapper>;
}
