import { useQuery } from "@apollo/client";
import { useEffect, useState } from "react";
import { GET_COLLECTIONS } from "../graphql/queries";
import { ProductItem } from "./ProductItem";
import { Grid } from "@mui/material";

interface IProduct {
  assets: {source: string}[];
  name: string;
  description: string
}
export function ProductList() {
  const {loading, error, data: {products}} = useQuery(GET_COLLECTIONS,{variables: {start: 0, limit: 10}})
  const [productList, setProductList] = useState(products?.items || [])


  useEffect(()=>{
    console.log(productList)
  }, [productList])
  return <Grid container spacing={2} >
        {productList?.map((item:IProduct,index:number)=>{
          return (<Grid item xs={3}>
          <ProductItem item={item} index={index} />
          </Grid>)
        })}

    </Grid>;
}
