import events from '../service/events';
export const useEventListener = (listener: any)=>{
    events.setListener(listener);

    const emit =()=>{
        const listeners = events.getListeners();
        listeners.map((func:any) => {
            func()
        })
    }
    return {
        emit
    }
}
