// Here we put queries. Remove next line
import { gql, useQuery } from '@apollo/client';

export const GET_COLLECTIONS = gql`
query ProductList($start: Int = 0, $limit: Int = 10) {
    products(options: {take: $limit, skip:$start}) {
		items {
            id
            name
            slug
            assets{
                source
            }
        }
    }
  }
`;
