import { useQuery } from "@apollo/client";
import { useEffect } from "react";
import { GET_COLLECTIONS } from "../graphql/queries";
import { ProductItem } from "./ProductItem";

interface IProduct {
  assets: {source: string}[];
  name: string;
  description: string
}
export function ProductList() {
  const {loading, error, data: {products:{items:productList = []}}} = useQuery(GET_COLLECTIONS,{variables: {start: 0, limit: 10}})

  useEffect(()=>{
    console.log(productList)
  }, [productList])
  return 
  <div style={{paddingTop: '15px', display: 'flex', flexWrap: 'wrap', justifyContent: 'space-evenly', width: '960px', margin: '0 auto'}}>
    {productList?.map((item:IProduct,index:number)=>{
      return (<ProductItem item={item} index={index} />)
    })}
  </div>;
}
