// Here we put queries. Remove next line
import { gql, useQuery } from '@apollo/client';

export const GET_COLLECTIONS = gql`
    query SearchProducts($term: String!, $skip: Int, $take: Int) {
    search(
      input: {
        term: $term,
        groupByProduct: true,
        skip: $skip,
        take: $take }
    ) {
      totalItems
      items {
        productName
        slug
        productAsset {
          id
          preview
        }
        priceWithTax {
          ... on SinglePrice {
            value
          }
          ... on PriceRange {
            min
            max
          }
        }
        currencyCode
      }
    }
  }
`;
