import { getApolloContext, useQuery } from "@apollo/client";
import { useContext, useEffect } from "react";
import { GET_COLLECTIONS } from "../graphql/queries";
import { styled } from "styled-components";

const Card = styled.article`

`
export function ProductList() {
  const {loading, error, data: {products:{items:productList}}} = useQuery(GET_COLLECTIONS,{variables: {start: 0, limit: 10}})
  //const {client} = useContext(getApolloContext());
  useEffect(()=>{
    console.log(productList)
  }, [productList])
  return <div>
    {productList?.map((item:any,index:number)=>{
      return (<article key={index}>
        <img src={item.assets[0].source} alt="product image" />
        <p>{item.name}</p>
      </article>)
    })}
  </div>;
}
