// Here we put queries. Remove next line
import { gql } from '@apollo/client';

export const GET_COLLECTIONS = gql`
query ProductList($start: Int, $limit: Int) {
    products(options: {take: $limit, skip:$start}) {
		items {
            id
            name
            slug
            assets{
                source
            }
            variants{
                name
                price
                sku
            }
        }

    }
  }
`;
