import { useState } from "react";


export default function useStateWithStorage(
  key: string,
  defaultValue: string = '[]'
) {
  const [initialValue, setInitialValue] = useState(JSON.parse(localStorage.getItem(key)||defaultValue))
  const addProduct = (item:ProductItem)=>{
    const cart = JSON.parse(localStorage.getItem(key)||defaultValue);

    localStorage.setItem(key, JSON.stringify(cart))
  }
  return {
    addProduct,
    initialValue
  }
}
